#ifndef RENDERINGMANAGER_HPP
#define RENDERINGMANAGER_HPP

	#include "RenderTriangle.hpp"
	#include "RenderRectangle.hpp"

	class RenderingManager
	{
		public:
			///
			/// Allows to access to the available element types.
			///
			static ShapeType type;

			///
			/// Constructor.
			///
			RenderingManager();

			///
			/// Destructor.
			///
			~RenderingManager();

			///
			/// Create a new and return a new buffered Shape. 
			///
			Element* getNewBufferedShape(unsigned char const);

		private:
			///
			/// Define all rendering element type
			/// that can be generated by the Manager.
			///
			enum ShapeType
			{
				SHAPE_TRIANGLE,
				SHAPE_RECTANGLE
			};

			///
			///
			///
			static GLuint buffers[];

			///
			///
			///
			Element* getNewShape(unsigned char const);

	};

#endif

